(2157, 'news', 101, 'az', 'keywords', 'gmo, qida, gömrük, tərkibində, ölkəyə, aşkar, bakı, idarəsinin, əlavəsinin, əlavəsi, külli, lesitin, alıblar, idarə, buraxılmasının, yükü, adlı, gətirilir, genetik, saxlanılıb'),
(2158, 'news', 101, 'az', 'title', 'Ölkəyə GMO gətirilir'),
(2159, 'news', 101, 'az', 'full', '<p style=\"text-align: justify;\"><span style=\"font-family:times new roman,serif;\"><span style=\"font-size:16.0pt;\">Tərkibində GMO elementləri olan k&uuml;lli miqdarda qida əlavəsi y&uuml;k&uuml; saxlanılıb.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:times new roman,serif;\"><span style=\"font-size:16.0pt;\">Bakı Baş G&ouml;mr&uuml;k İdarəsinin əməkdaşları tərkibində genetik modifikasiya olunmuş orqanizmlərin (GMO) aşkar edildiyi k&uuml;lli miqdarda qida əlavəsinin d&ouml;vriyyəyə buraxılmasının qarşısını alıblar.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:times new roman,serif;\"><span style=\"font-size:16.0pt;\">Bu fakt idarə əməkdaşlarının &quot;Lesitin Soyevıy&quot; adlı qida əlavəsi y&uuml;k&uuml;nə g&ouml;mr&uuml;k nəzarəti zamanı qeydə alınıb. Həmin y&uuml;k Rusiya Federasiyasından 90 RO 897/10 ZE 332 n&ouml;mrə nişanlı avtomobillə Bakı şəhəri, Yasamal rayonu ərazisində yerləşən &quot;Rusarom&quot; MMC-nin &uuml;nvanına daxil olub.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:times new roman,serif;\"><span style=\"font-size:16.0pt;\">Mərkəzi G&ouml;mr&uuml;k Ekspertizası İdarəsinin arayışına əsasən, cəmi 5425 kq olan Braziliya mənşəli qida əlavəsinin tərkibində GMO elementləri aşkar edilib.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:times new roman,serif;\"><span style=\"font-size:16.0pt;\">Faktla bağlı araşdırma aparılır.</span></span></p>'),
(2160, 'news', 101, 'az', 'short', 'Ölkəyə GMO gətirilir'),
